	"MODELO" VARCHAR2(50 BYTE), 
	"MONTADORA" VARCHAR2(50 BYTE), 
	"PLACA" VARCHAR2(15 BYTE), 
	"ANO" NUMBER, 
	 PRIMARY KEY ("ID")
   );